import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { validateOTP } from '../../utils/helpers';

const OTPVerification = () => {
  const [otp, setOtp] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [registrationData, setRegistrationData] = useState(null);
  const { register } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // Get registration data from session storage
    const storedData = sessionStorage.getItem('registrationData');
    if (storedData) {
      setRegistrationData(JSON.parse(storedData));
    } else {
      // No registration data, redirect to register
      navigate('/register');
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!validateOTP(otp)) {
      setError('Please enter a valid 6-digit OTP');
      return;
    }

    if (!registrationData) {
      setError('Registration data not found. Please try again.');
      return;
    }

    setIsLoading(true);
    const result = await register(registrationData.email, registrationData.name, otp);

    if (result.success) {
      // Clear registration data
      sessionStorage.removeItem('registrationData');
      navigate('/chat');
    } else {
      setError(result.error);
    }

    setIsLoading(false);
  };

  const handleOtpChange = (e) => {
    const value = e.target.value.replace(/\D/g, '').slice(0, 6);
    setOtp(value);
  };

  if (!registrationData) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h1 className="auth-title">Verify Your Email</h1>
        <p className="auth-subtitle">
          We've sent a 6-digit code to <strong>{registrationData.email}</strong>
        </p>

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="otp">OTP Code</label>
            <input
              id="otp"
              type="text"
              value={otp}
              onChange={handleOtpChange}
              placeholder="Enter 6-digit OTP"
              maxLength="6"
              required
              disabled={isLoading}
              className="otp-input"
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button type="submit" className="btn btn-primary" disabled={isLoading || otp.length !== 6}>
            {isLoading ? 'Verifying...' : 'Verify OTP'}
          </button>
        </form>

        <p className="auth-link">
          <button 
            type="button" 
            className="link-button" 
            onClick={() => navigate('/register')}
          >
            Back to Register
          </button>
        </p>
      </div>
    </div>
  );
};

export default OTPVerification;
